
class Node:
    def __init__(self, symbol=None, weight=0, parent=None):
        self.symbol = symbol      # int 0-255 o 'NYT' o None
        self.weight = weight
        self.parent = parent
        self.left = None
        self.right = None

    def is_leaf(self):
        return self.left is None and self.right is None
